css:
* {
  margin: 0;
  padding: 0;
}
body {
  margin: 0;
  padding: 0;
  -webkit-box-sizing: border-box;
  box-sizing: border-box;
}
.barcode {
  margin: 80px 0;
}
.barcode:first-child {
  margin: 0;
}
.barcode:last-child {
  margin: 0;
}
.barcode .head {
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  -webkit-box-align: center;
  -ms-flex-align: center;
  align-items: center;
  -webkit-box-pack: justify;
  -ms-flex-pack: justify;
  justify-content: space-between;
  margin: 15px;
}
.barcode .head p {
  color: #000;
  font-size: 18px;
  font-weight: 500;
}
.barcode .body .barcode-image img {
  width: 100%;
  height: 150px;
  -o-object-fit: fill;
  object-fit: fill;
}
.barcode .foot .company-name {
  text-align: center;
}
.barcode .foot .company-name h3 {
  color: #000;
  font-size: 40px;
}
