css:
body {
  padding: 0;
  margin: 0;
}

.print-format td, .print-format th {
    padding: 3px 10px !important;
}

.container-2 {
  width: 100%;
  margin: 0;
  display: flex;
  padding: 10px 0px;
}

.container-2 .first {
  width: 100%;
}
.first .data label{
  width: 200px;
}

table,
th {
  border: 1px solid black;
  border-collapse: collapse;
}

.color-pic{
    width: 100%;
    background-color: #3370a8;
}

.color-pic th:first-child{
    width: 60%;
}

.head-table{
    background-color: #deeaf6;
}

.f-tbl-td{
    background-color: #d0cece;
}


#change-color {
    background-color: #d5dce4 !important;
}

.f-td{
    width: 120px !important;
}

.color-pic th{
    font-weight: bold;
    text-align: center;
    color: #fff;
}

th,
td {
  padding-left: 10px;
  border: 1px solid black;
  font-weight: 400;
  color: #000;
}

#sec-tbl{
    margin-top: 25px;
}

#sec-tbl th{
    background-color: #bdd6ee;
}

.center {
  text-align: center;
}
.left {
    font-weight: bold;
  text-align: end !important;
  width: 20%;
}

.right{
   width: auto;
}

.head {
    display: flex;
    align-items: center;
    justify-content: space-between;
    margin-bottom: 50px;
    padding: 0 15px;
}

.head h3{
    color: #000;
    font-size: 30px;
}

.f-tr th{
    color: #000;
    font-weight: bold;
    text-align: center
}


tfoot tr td{
    font-weight: bold;
}

tfoot tr{
    background-color: #ededed;
}

tfoot tr:first-child,
tfoot tr:last-child{
    background-color: #bdd6ee !important;
}


#term-cond{
    margin: 50px 0;
}

.container .head{
    margin: 0;
}

.container h4{
    color: #000;
    font-size: 20px;
    font-weight: bold;
}


ul{
 margin-left: 20px;   
}

ul li{
    margin: 5px;
    font-weight: 500;
}
.image img{
        width: 118px;
}

.img img{
    width: 250px;
    float: right;
    margin-right: 58px;
}

#foot h4{
    color: #000;
    font-size: 25px;
    font-weight: 400;
}

#foot p{
    font-weight: 500;
    font-size: 18px;
}
