css:
@page {
    margin: 0;

  }
 * {
    margin: 0%;
    padding: 0%;
    font-family: "Gill Sans", "Gill Sans MT", Calibri, "Trebuchet MS",
      sans-serif;
  }


  .background {
    position: fixed;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    z-index: 10;
  }

  @media print{
    .mt-50{
        margin-top:150px;
    }

    .background {
      display:block;
    }
  }

  .background img {
    width: 100vw;
    height: 100vh;
  }

  .flex {
    display: flex;
    justify-content: space-between;
    align-items: center;
  }

  .print-format {
    width: 100%;
    padding: 0 15px;
    background-color: transparent;
  }

  .print-format > table {
    width: 100%;
    position: relative;
    z-index: 1000;
  }

  .table1 {
    margin-bottom: 20px;
  }

  .table2 {
    margin-bottom: 20px;
  }



.items th {

    background-color: #13395a !important;
    color:white !important;
}

table { page-break-after:auto; font-size: 12px; }
tr    { page-break-inside:auto; page-break-after:auto }
td    { page-break-inside:auto; page-break-after:auto }
thead { display: table-header-group; }
tfoot { display: table-footer-group;}

.invoice-details, 
.invoice-details tr,
.invoice-details th,
.invoice-details td
{
    padding: 0 !important;
    background: transparent !important;
}

.invoice-details td 
{
    padding-left: 10px !important;
}
.table1 .details,
.table2 .details
{
    width: 35%;
}

.mb-0 {
    margin-bottom: 0 !important;
}

table.mb-0,
.last-row td
{
    border-bottom: none !important;
}


.mt-0 {
    margin-top: 0 !important;
}
.submit-stamp{
    height: 200px;
    width: 200px;
    max-width: 40%;
    margin-left: 50%;
    transform: rotate(30deg);
}
