css:
@page {
    padding: 0;
    margin: 0;
}

.print-format {
    padding: 33px  !important;
    color: #564B65;
    --text-color: #564B65;
}

.customer-address {
    flex: 1;
    width: 50%;
}

.document-refrences {
    flex: 1;
    margin-left: 25%;
}

table {
    width: 100%;
    color: #564B65;
}

h3 {
    color: #3E86BD !important;
    margin-top: 0 !important;
}

.print-format td,
.print-format th {
    padding: 3px !important;
}

.items-table {
    margin-top: 20px;
}


.items-table tr td:first-child,
.items-table tr th:first-child
{
    border-radius: 50px 0px 0px 50px !important;
}

.items-table tr td:last-child,
.items-table tr th:last-child
{
    border-radius: 0px 50px 50px 0px !important;
}

.items-table tr td {
    padding: 2px 20px !important;
}

.items-table tr.background th {
    padding: 5px 20px !important;
}

.items-table tr.background td {
    padding: 8px 20px !important;
}
.items-table tr.background:nth-child(even) {
    background-color: #C0DFEE;
}

.items-table th {
    color: #fff;
    font-weight: bold;
    text-align: center;
    background-color: #3C82BB;
}

#stamp img {
    width: 180px !important;
}

#sign img {
    width: 118px !important;
}


.d-flex {
    display: flex;
}

.justify-between {
    justify-content: space-between;
    align-items: center;
}


    @media print {
        table {
            width: 100%;
            border-collapse: collapse;
        }

        thead {
            display: table-header-group !important;
            /* Ensures headers are repeated on each page */
        }

        tfoot {
            display: table-footer-group !important;
            /* Ensures footers are repeated on each page */
        }

        tbody {
            display: table-row-group !important;
        }


    }

.page-break-after {
    page-break-after: always !important;
}

.page-break-insude {
    page-break-after: auto !important;
}
