html:
{% if letter_head and not no_letterhead -%}
  <div class="letter-head">{{ letter_head }}</div>
{%- endif %}

<div class="intro">
    <div class="table1">
    <table>
            <tr>
               <td> <strong>CLIENT</strong>  </td> 
               <td>  {{doc.customer_name}}   </td>
           </tr>
            <tr>
               <td> <strong>PROJECT NAME</strong>  </td> 
               <td>  {{doc.project}}   </td>
           </tr>
           <tr>
               <td> <strong>TERRITORY</strong>  </td> 
               <td>  {{doc.territory}}   </td>
           </tr>
        </table>
    </div>
    <div class="table2">
        <table>
            <tr>
               <td> <strong>DATE</strong>  </td> 
               <td>  {{doc.posting_date}}   </td>
           </tr>
            <tr>
               <td> <strong>QTN</strong>  </td> 
               <td>  {{doc.name}}   </td>
           </tr>
           <tr>
               <td> <strong>CONTRACT</strong>  </td> 
               <td>  {{doc.name}}   </td>
           </tr>
        </table>
    </div>
</div>

<div class="items">
    <table class="table table-bordered">
      <thead>
        <tr>
          <th width="5%">ITEM</th>
          <th width="35%">DESCRIPTION</th>
          <th width="10%">UNIT</th>
          <th width="10%">QTY</th>
          <th width="20%">UNIT PRICE</th>
          <th width="20%">AMOUNT</th>
        </tr>
        <tr>
            <th colspan="6">DESIGN</th>
        </tr>
      </thead>
      <tbody>
        {%- for row in doc.items -%}
        <tr>
          <td>{{ row.idx }}</td>
          <td>{{ row.description }}</td>
          <td>{{ row.qty }}</td>
          <td>{{ row.qty }}</td>
          <td>{{ row.get_formatted("rate", doc) }}</td>
          <td>{{ row.get_formatted("amount", doc) }}</td>
        </tr>
        {%- endfor -%}
        <tr>
            <td colspan="4" style="text-align:left; border-color: transparent;"><i>Final Price:{{ doc.in_words }}</i></td>
            <td style="border-color: transparent; border-right-color: #d1d8dd; text-align:right;">SUBTOTAL</td>
            <td>{{ doc.get_formatted("total", doc) }}</td>
        </tr>
        <tr>
            <td colspan="4" style="text-align:left; border-color: transparent;">: قم حساب الموءسس</td>
            <td style="border-color: transparent; border-right-color: #d1d8dd; text-align:right;">VAT AMOUNT</td>
            <td>{{ doc.get_formatted("total_taxes_and_charges", doc) }}</td>
        </tr>  
        <tr>
            <td colspan="4" style="text-align:left; border-color: transparent;">9w8rwer987wer7tur0</td>
            <td style="border-color: transparent; border-right-color: #d1d8dd; text-align:right;">TOTAL</td>
            <td>{{ doc.get_formatted("grand_total", doc) }}</td>
        </tr>  
      </tbody>
    </table>
</div>

<div class="terms">
    <table>
        <tr>
            <td width="100%">مدت مشرعو یوم عمل</td>
        </tr>
        <tr>
            <td width="100%">{{ doc.tc_name }}</td>
        </tr>
        <tr>
            <td width="100%">{{ doc.terms }}</td>
        </tr>
    </table>
</div>

<div class="footer">
    <p style="text-align:center">If you have any questions concerning this quotation, kindly contact Naif AlTurki, 0550 667 4548</p>
    <h3 style="text-align:center">THANK YOU FOR YOUR BUSINESS!</h3>
</div>

<div id="footer-html" class="visible-pdf">
    {% if footer and not no_letterhead %}
    <div id="letter-head-footer">
        {{ footer }}
    </div>
    {% endif %}
</div>

<style>
  * {
    margin: 0;
    padding: 0;
    font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
    font-size: 16px;
  }
  .intro {
    display: flex;
    width: 100%;
    font-size: 18px;
  }
  .table1 {
    width: 65%;
  }
  .items {
    margin-top: 30px;
    width: 100%;
  }
  .items table {
    width: 100%;
    text-align: center;
  }
  .items table th {
    text-align: center;
  }
  .terms {
    margin-top: 50px;
  }
  .terms table {
    width: 100%;
    text-align: right;
  }
  .footer {
    margin-top: 50%;
  }
  .footer p {
    font-size: 18px;
  }

  @media print {
    .letter-head, .footer {
      position: fixed;
      margin:20px;
      width: 100%;
      left: 0;
      right: 0;
    }
    .letter-head {
      top: 0;
    }
    .footer {
      bottom: 0;
    }
    body {
      margin-top: 150px; /* Adjust depending on header height */
      margin-bottom: 100px; /* Adjust depending on footer height */
    }
    .items {
      page-break-inside: avoid;
    }
    .terms {
      page-break-before: avoid;
      page-break-after: avoid;
    }
  }
</style>
